"use strict";const isLinux=()=>"linux"===process.platform;let report=null;const getReport=()=>{if(!report)if(isLinux()&&process.report){const e=process.report.excludeNetwork;process.report.excludeNetwork=!0,report=process.report.getReport(),process.report.excludeNetwork=e}else report={};return report};module.exports={isLinux:isLinux,getReport:getReport};